from mysql.connector import MySQLConnection
#import pyodbc
#import MySQLdb


class Connection(object):
    @classmethod
    def create(cls, driver, connection_string):
        if driver == 'mysqlconnector':
            return MySQLConnection(**connection_string)
        elif driver == 'pyodbc':
            return pyodbc.connect(connection_string)
        elif driver == 'pymysql':
            # host=self.host, user=self.user, passwd=self.passwd, db=self.db, charset='utf8'
            return pymysql.connect(**connection_string)
        elif driver == 'MySQLdb':
            return MySQLdb.connect(**connection_string)
        else:
            return MySQLConnection(**connection_string)


