class ObjectDict(dict):
    def __init__(self, columns, row_tuple):
        super(ObjectDict, self).__init__(zip(tuple([column[0] for column in columns]), row_tuple))

    def __getattr__(self, name):
        if name in self:
            return self[name]
        else:
            raise AttributeError("No such attribute: " + name)

    def __setattr__(self, name, value):
        self[name] = value

    def __delattr__(self, name):
        if name in self:
            del self[name]
        else:
            raise AttributeError("No such attribute: " + name)

